import React, { useState, useEffect } from 'react';
import { read, utils } from 'xlsx';

const ExcelSearch = () => {
  const [products, setProducts] = useState([]); // Store Excel data
  const [searchTerm, setSearchTerm] = useState(''); // Store search input

  // Load Excel file from public folder
  useEffect(() => {
    const fetchExcelData = async () => {
      const response = await fetch('./Assets/Dataset.xslx'); // Adjust the path if necessary
      const arrayBuffer = await response.arrayBuffer(); // Fetch the file as binary data
      const workbook = read(arrayBuffer, { type: 'array' }); // Parse the Excel file
      const sheet = workbook.Sheets[workbook.SheetNames[0]]; // Get the first sheet
      const jsonData = utils.sheet_to_json(sheet); // Convert sheet to JSON
      setProducts(jsonData); // Save JSON data in state
    };

    fetchExcelData();
  }, []);

  const filteredProducts = products.filter((product) =>
    product.Product_Name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Search Products</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search for a product"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Display Filtered Results */}
      <ul>
        {filteredProducts.map((product, index) => (
          <li key={index}>
            {product.Product_ID}: {product.Product_Name} - {product.Quantity}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExcelSearch;
