import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
    const [menu, setMenu] = useState("Shop");

    return (
        <div className="navbar">
            <div className="nav-logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="nav-menu">
                <li onClick={() => setMenu("Shop")}>
                    <Link to="/">Shop</Link>
                    {menu === "Shop" && <hr />}
                </li>
                <li onClick={() => setMenu("Food")}>
                    <Link to="/Food">Food</Link>
                    {menu === "Food" && <hr />}
                </li>
                <li onClick={() => setMenu("Clothes")}>
                    <Link to="/Clothes">Clothes</Link>
                    {menu === "Clothes" && <hr />}
                </li>
                <li onClick={() => setMenu("Necessities")}>
                    <Link to="/Necessities">Necessities</Link>
                    {menu === "Necessities" && <hr />}
                </li>
            </ul>
            <div className="nav-login-cart">
                <Link to="/login"><button>Login</button></Link> {/* Use Link */}
                <Link to="/cart"><img src={cart_icon} alt="Cart Icon" /></Link> {/* Use Link */}
                <div className="nav-cart-count">0</div>
            </div>
        </div>
    );
};

export default Navbar;
