import React, { useState } from 'react';

function SearchBar() {
  const [searchQuery, setSearchQuery] = useState("");
  const [message, setMessage] = useState("");

  // Updated items array to include names and image URLs
  const items = [
    { name: "Apple", imageUrl: "https://via.placeholder.com/100x100.png?text=Apple" },
    { name: "Banana", imageUrl: "https://via.placeholder.com/100x100.png?text=Banana" },
    { name: "Orange", imageUrl: "https://via.placeholder.com/100x100.png?text=Orange" },
    { name: "Mango", imageUrl: "https://via.placeholder.com/100x100.png?text=Mango" },
    { name: "Grape", imageUrl: "https://via.placeholder.com/100x100.png?text=Grape" }
  ];

  const handleChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearch = () => {
    const filteredItems = items.filter(item =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    if (filteredItems.length === 0) {
      setMessage("Product not found.");
    } else {
      setMessage("");  // Clear the message if products are found
    }
  };

  const filteredItems = items.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={handleChange}
        style={{
          padding: '8px',
          width: '200px',
          borderRadius: '4px',
          border: '1px solid #ccc'
        }}
      />
      <button onClick={handleSearch} style={{ padding: '8px', marginLeft: '8px' }}>
        Search
      </button>

      <ul>
        {filteredItems.length > 0 ? (
          filteredItems.map((item, index) => (
            <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <img
                src={item.imageUrl}
                alt={item.name}
                style={{ width: '50px', height: '50px', marginRight: '10px' }}
              />
              {item.name}
            </li>
          ))
        ) : (
          <li>No items found</li>
        )}
      </ul>

      {message && (
        <p style={{ color: 'red', marginTop: '10px' }}>{message}</p>
      )}
    </div>
  );
}

export default SearchBar;
